mapper.select:

	 select pdt_num_pk, cate_code, cate_code_prt, pdt_name, pdt_price, pdt_discount, pdt_company, pdt_detail, pdt_img, pdt_amount, pdt_buy, pdt_date_sub, pdt_date_up
  	 from 
      (
      select /*+INDEX_DESC(product_tbl SYS_C007541) */
        rownum rn,  pdt_num_pk, cate_code, cate_code_prt, pdt_name, pdt_price, pdt_discount, pdt_company, pdt_detail, pdt_img, pdt_amount, pdt_buy, pdt_date_sub, pdt_date_up
      from 
        product_tbl
        where rownum <= #{cri.pageNum} * #{cri.amount} and cate_code = #{cate_code}

      )

  where rn > (#{cri.pageNum} -1) * #{cri.amount}
